        bytes32 campaignId
    ) external view returns (PublicCampaign memory) {
        PublicCampaign memory campaign = openCampaignInfo[campaignId];
        if (campaign.creatorAddress == address(0)) {
            revert CampaignNotFound(campaignId);
        }
        return campaign;
    }

